INSERT INTO `webform_submitted_data` (`nid`,`sid`,`cid`,`no`,`data`) VALUES (10,149,19,'0','Spreadsheets: CityLandUse, CommunityProviderLandUse, HUC_12_LandUse, Combined\r\n\r\nField Definitions:\r\nName: Name of the municipality\r\nTotal_Area: total area of the municipality.\r\narea_IR: Area of irrigated land\r\narea_NI: Area of non-irrigated land\r\narea_RIP/W: Area of Riparian vegetation/water\r\narea_Farmstead: Area of Farmstead land use\r\narea_residential: Area of residential land use\r\narea_commind: Area of commercial, industrial land use\r\narea_parkOS: Area of parks or open space\r\narea_blank: Area in acres that had Water_Related Land Use data, subracted from total area in acres\r\n\r\n\r\nTotal_WRLU: Total of areas that had Water-Related Land Use data in acres\r\n\r\n\r\nGAMUT: iUtah GAMUTS 1= Logan   2= Red Butte Creek  3 = Heber \r\n\r\nIrrigated: Percentage. Irrigated Agriculture, in acres,  divided by Total_WRLU\r\nNon-Irrigated:Non-IRRIGATED Agriculture, in acres, divided by Total_WRLU\r\nRip/W: Riparian/Water Features, in acres, divided by Total_WRLU\r\nFarmstead: Farmstead area, in acres, divided by Total_WRLU\r\nCommercial/Industrial:Commercial/Industrial totals in acres, divided by Total_WRLU\r\nResidential:Residential totals, in acres, divided by Total_WRLU\r\nParks/OS:Total area, in acres, of Parks and open spaces divided by Total_WRLU\r\n\r\n\r\n\r\n\r\n');
